import React , { Component } from 'react';

import Aux from '../../hoc/aux';
import Burger from '../../components/burger/burger';
import Controls from '../../components/burger/controls/controls';
import Modal from '../../components/UI/modal/modal';
import Summary from '../../components/burger/summary/summary';
import axios from '../../axios-orders';
import Spinner from '../../components/UI/spinner/spinner';
import withErrorHandler from '../../hoc/withErrorHandler/withErrorHandler';

const PRICES = {
    salad: 0.5,
    cheese: 1.0,
    meat: 3.0,
    bacon: 2.0
}

class Builder extends Component {
    state = {
        ingredients: null,
        totalPrice: 0,
        canPurchase: false,
        purchasing: false,
        loading: false
    }

    componentDidMount() {
        axios.get('https://burger-builder-ea013.firebaseio.com/ingredients.json')
            .then(res => {
                this.setState({ingredients: res.data});
            }).catch(err => {
                
            });
    }

    purchaseHandler = () => {
        this.setState({
            purchasing: true
        });
    }

    addIngredientHandler = (type) => {
        const count = this.state.ingredients[type] + 1;
        const updatedIng = {
            ...this.state.ingredients,
        };
        updatedIng[type] = count;
        const newPrice = this.state.totalPrice + PRICES[type]
        this.setState({
            totalPrice: newPrice,
            ingredients: updatedIng
        });
        this.updateCanPurchase(updatedIng);
    }

    updateCanPurchase = (ingredients) => {
        const sum = Object.keys(ingredients)
        .map((key) => {
            return ingredients[key];
        }).reduce((sum, el) => {
            return sum + el;
        }, 0);
        this.setState({canPurchase: sum > 0});
    }

    removeIngredientHandler = (type) => {
        const count = this.state.ingredients[type] - 1;
        if(count < 0){
            return;
        }
        const updatedIng = {
            ...this.state.ingredients,
        };
        updatedIng[type] = count;
        const newPrice = this.state.totalPrice - PRICES[type]
        this.setState({
            totalPrice: newPrice,
            ingredients: updatedIng
        });
        this.updateCanPurchase(updatedIng);
    }

    //use this to cancel purchases in general
    closeModalHandler = () => {
        this.setState({purchasing: false});
    }

    continuePurchaseHandler = () => {
        //alert('you continue!');
        //.json is for firebase only
        //price should actually be calculated on the server
        this.setState({loading: true});
        const order = {
            ingredients: this.state.ingredients,
            price: this.state.price,
            customer: {
                name: 'Steven',
                address: '10 Winner Street'
            }
        }
        axios.post('/orders.json', order)
            .then(res => {
                console.log(res);
                this.setState({
                    loading: false,
                    purchasing: false
                });
            })
            .catch(error => {
                console.log(error);
                this.setState({
                    loading:false,
                    purchasing: false
                });
            });
    }

    render() {
        const disabled = {
            ...this.state.ingredients
        }
        for(let key in disabled){
            disabled[key] = disabled[key] <= 0;
        }
        let orderSummary = null;
        let burger = <Spinner/>
        if(this.state.ingredients != null){
            burger = (
                <Aux>
                    <Burger ingredients={this.state.ingredients}/>
                    <Controls ordered={this.purchaseHandler} price={this.state.totalPrice} 
                    addIng={this.addIngredientHandler} remIng={this.removeIngredientHandler}
                    disabled={disabled} canPurchase={this.state.canPurchase}/>
                </Aux>
            );
            orderSummary = <Summary ingredients={this.state.ingredients} canceled={this.closeModalHandler}
            continued={this.continuePurchaseHandler} price={this.state.totalPrice}/>;
            if(this.state.loading){
                orderSummary = <Spinner/>;
            }
        }
        return (
            <Aux>
                <Modal show={this.state.purchasing} closeModal={this.closeModalHandler}>
                    {orderSummary}
                </Modal>
                {burger}
            </Aux>
        );
    }
}

export default withErrorHandler(Builder,axios);
