import React , { Component } from 'react';
import { connect } from 'react-redux';

import Aux from '../../hoc/aux';
import Burger from '../../components/burger/burger';
import Controls from '../../components/burger/controls/controls';
import Modal from '../../components/UI/modal/modal';
import Summary from '../../components/burger/summary/summary';
import axios from '../../axios-orders';
import Spinner from '../../components/UI/spinner/spinner';
import withErrorHandler from '../../hoc/withErrorHandler/withErrorHandler';
import * as actionTypes from '../../store/actions';

class Builder extends Component {
    state = {
        totalPrice: 0,
        canPurchase: false,
        purchasing: false,
        loading: false,
        error: false
    }

    componentDidMount() {
        /*axios.get('https://burger-builder-ea013.firebaseio.com/ingredients.json')
            .then(res => {
                this.setState({ingredients: res.data});
            }).catch(err => {
                this.setState({error: true});
            });*/
    }

    purchaseHandler = () => {
        this.setState({
            purchasing: true
        });
    }

    /*addIngredientHandler = (type) => {
        const count = this.state.ingredients[type] + 1;
        const updatedIng = {
            ...this.state.ingredients,
        };
        updatedIng[type] = count;
        const newPrice = this.state.totalPrice + PRICES[type]
        this.setState({
            totalPrice: newPrice,
            ingredients: updatedIng
        });
        this.updateCanPurchase(updatedIng);
    }*/

    updateCanPurchase = (ingredients) => {
        const sum = Object.keys(ingredients)
        .map((key) => {
            return ingredients[key];
        }).reduce((sum, el) => {
            return sum + el;
        }, 0);
        this.setState({canPurchase: sum > 0});
    }

    /*removeIngredientHandler = (type) => {
        const count = this.state.ingredients[type] - 1;
        if(count < 0){
            return;
        }
        const updatedIng = {
            ...this.state.ingredients,
        };
        updatedIng[type] = count;
        const newPrice = this.state.totalPrice - PRICES[type]
        this.setState({
            totalPrice: newPrice,
            ingredients: updatedIng
        });
        this.updateCanPurchase(updatedIng);
    }*/

    //use this to cancel purchases in general
    closeModalHandler = () => {
        this.setState({purchasing: false});
    }

    continuePurchaseHandler = () => {
        console.log("continuePurchaseHandler called in builder.js");
        const queryParams = [];
        for(let i in this.state.ingredients){
            queryParams.push(encodeURIComponent(i) + '=' + encodeURIComponent(this.state.ingredients[i]));
        }
        queryParams.push('price=' + this.state.totalPrice);
        const query = queryParams.join('&');
        this.props.history.push({
            pathname: "/checkout",
            search: '?' + query
        });
    }

    render() {
        const disabled = {
            ...this.props.ings
        }
        for(let key in disabled){
            disabled[key] = disabled[key] <= 0;
        }
        let orderSummary = null;
        let burger = this.state.error ? <p>Ingredients cannot be loaded</p> : <Spinner/>
        if(this.props.ings != null){
            burger = (
                <Aux>
                    <Burger ingredients={this.props.ings}/>
                    <Controls ordered={this.purchaseHandler} price={this.state.totalPrice} 
                    addIng={this.props.onIngredientAdded} remIng={this.props.onIngredientRemoved}
                    disabled={disabled} canPurchase={this.state.canPurchase}/>
                </Aux>
            );
            orderSummary = <Summary ingredients={this.props.ings} canceled={this.closeModalHandler}
            continued={this.continuePurchaseHandler} price={this.state.totalPrice}/>;
            if(this.state.loading){
                orderSummary = <Spinner/>;
            }
        }
        return (
            <Aux>
                <Modal show={this.state.purchasing} closeModal={this.closeModalHandler}>
                    {orderSummary}
                </Modal>
                {burger}
            </Aux>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        ings: state.ingredients,
        price: state.totalPrice
    };
}

const mapDispatchToProps = (dispatch) => {
    return {
        onIngredientAdded: (ingName) => dispatch({type: actionTypes.ADD_INGREDIENT, 
            ingredientName: ingName}),
        onIngredientRemoved: (ingName) => dispatch({type: actionTypes.REMOVE_INGREDIENT,
            ingredientName: ingName})
    };
}

export default connect(mapStateToProps,mapDispatchToProps)(withErrorHandler(Builder,axios));
