import React, { Component } from 'react';

import CheckoutSummary from '../../components/order/checkoutSum';
import { Route } from 'react-router-dom';
import ContactData from './contactData';

import { connect } from 'react-redux';

class Checkout extends Component {
    cancelCheckoutHandler = () => {
        this.props.history.goBack();
    }

    checkoutContinuedHandler = () => {
        //load contact data component
        console.log("checkoutContinued handler ran");
        this.props.history.replace(this.props.match.path + '/contactdata');
    }

    render() {
        return (
            <div>
                {console.log(this.props.ings)}
                <CheckoutSummary ingredients={this.props.ings}
                    cancelCheckout={this.cancelCheckoutHandler}
                    checkoutContinued={this.checkoutContinuedHandler}/>
                <Route path={this.props.match.path + '/contactdata'} render={(props) => (
                    <ContactData ingredients={this.state.ingredients} price={this.state.totalPrice} {...props}/>
                )}/>
            </div>
        );
    }
};

const mapStateToProps = (state) => {
    return {
        ings: state.ingredients,
        price: state.totalPrice
    }
};

//no need for dispatch

export default connect(mapStateToProps,null)(Checkout);
