import * as actionTypes from '../actions/actionTypes';

const initialState = {
    orders: [],
    loading: false
};

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case actionTypes.PURCHASE_SUCCESS:
            const newOrder = {
                ...action.order,
                id: action.id
            }
            return {
                ...state,
                loading: false,
                orders: state.orders.concat(newOrder)
            };
        case actionTypes.PURCHASE_FAIL:
            //error is handled in ContactData.js with withErrorHandler()
            return {
                ...state,
                loading: false
            };
        case actionTypes.PURCHASE_START:
            return {
                ...state,
                loading: true
            };
        default:
            return state;
    }
};
