import React, { Component } from 'react';

import CheckoutSummary from '../../components/order/checkoutSum';
import { Route } from 'react-router-dom';
import ContactData from './contactData';

class Checkout extends Component {
    state={
        ingredients: null,
        totalPrice: 0
    }

    constructor(props){
        super(props);
        const query = new URLSearchParams(this.props.location.search);
        const ing = {};
        let price = 0;
        for (let param of query.entries()){
            if(param[0] === 'price'){
                price = param[1];
            }else{
                ing[param[0]] = +param[1];
            }
        }
        console.log(ing);
        this.setState({
            ingredients: ing,
            totalPrice: price
        });
    }

    /*componentDidMount() {
        const query = new URLSearchParams(this.props.location.search);
        const ing = {};
        let price = 0;
        for (let param of query.entries()){
            if(param[0] === 'price'){
                price = param[1];
            }else{
                ing[param[0]] = +param[1];
            }
        }
        this.setState({
            ingredients: ing,
            totalPrice: price
        });
    }*/

    cancelCheckoutHandler = () => {
        this.props.history.goBack();
    }

    checkoutContinuedHandler = () => {
        //load contact data component
        console.log("checkoutContinued handler ran");
        this.props.history.replace(this.props.match.path + '/contactdata');
    }

    render() {
        return (
            <div>
                <CheckoutSummary ingredients={this.state.ingredients}
                    cancelCheckout={this.cancelCheckoutHandler}
                    checkoutContinued={this.checkoutContinuedHandler}/>
                <Route path={this.props.match.path + '/contactdata'} render={() => (
                    <ContactData ingredients={this.state.ingredients} price={this.state.totalPrice}/>
                )}/>
            </div>
        );
    }
};

export default Checkout;
