import React , {Component} from 'react';

import Modal from '../../components/UI/modal/modal';
import Aux from '../aux';

const withErrorHandler = (Wrapped , axios) => {
    return class extends Component {
        render(){
        return (
            <Aux>
                <Modal show>
                    Error
                </Modal>
                <Wrapped {...props}/>
            </Aux>
        );
        }
    };
}

export default withErrorHandler;
